import React, { useState } from 'react';
import axios from 'axios';

const StudentForm = () => {
  const [student, setStudent] = useState({
    studentId: '',
    firstName: '',
    lastName: '',
    email: '',
    dob: '',
    department: '',
    course: '',
    enrollmentYear: '',
    isActive: true,
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setStudent({ ...student, [name]: type === 'checkbox' ? checked : value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/students', student);
      alert('Student added successfully!');
      setStudent({
        studentId: '',
        firstName: '',
        lastName: '',
        email: '',
        dob: '',
        department: '',
        course: '',
        enrollmentYear: '',
        isActive: true,
      });
    } catch (error) {
      console.error(error);
      alert('Error adding student');
    }
  };

  return (
    <div>
      <h2>Add Student</h2>
      <form onSubmit={handleSubmit}>
        <input name="studentId" value={student.studentId} onChange={handleChange} placeholder="Student ID" required /><br />
        <input name="firstName" value={student.firstName} onChange={handleChange} placeholder="First Name" required /><br />
        <input name="lastName" value={student.lastName} onChange={handleChange} placeholder="Last Name" required /><br />
        <input type="email" name="email" value={student.email} onChange={handleChange} placeholder="Email" required /><br />
        <input type="date" name="dob" value={student.dob} onChange={handleChange} placeholder="DOB" required /><br />
        <input name="department" value={student.department} onChange={handleChange} placeholder="Department" required /><br />
        <input name="course" value={student.course} onChange={handleChange} placeholder="Course" required /><br />
        <input name="enrollmentYear" value={student.enrollmentYear} onChange={handleChange} placeholder="Enrollment Year" required /><br />
        <label>
          Active: <input type="checkbox" name="isActive" checked={student.isActive} onChange={handleChange} />
        </label><br />
        <button type="submit">Add Student</button>
      </form>
    </div>
  );
};

export default StudentForm;
