import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import './FormStyles.css';

function EditStudent() {
  const [student, setStudent] = useState({
    studentId: '',
    firstName: '',
    lastName: '',
    email: '',
    dob: '',
    course: '',
    enrollmentYear: '',
    status: 'Active',
  });

  const navigate = useNavigate();
  const { id } = useParams();

  useEffect(() => {
    axios.get(`http://localhost:5000/students/${id}`)
      .then(res => setStudent(res.data))
      .catch(err => alert('Error fetching student: ' + err.message));
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;

    if (name === "studentId" || name === "enrollmentYear") {
      setStudent({ ...student, [name]: Number(value) });
    } else {
      setStudent({ ...student, [name]: value });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    axios.put(`http://localhost:5000/students/${id}`, student)
      .then(() => {
        alert('Student updated successfully!');
        navigate('/students');
      })
      .catch(err => {
        console.error(err.response?.data || err.message);
        alert('Error updating student: ' + (err.response?.data?.error || err.message));
      });
  };

  return (
    <div className="form-container">
      <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>Edit Student</h2>
      <form onSubmit={handleSubmit} className="student-form">

        <div className="form-group">
          <label>Student ID</label>
          <input
            name="studentId"
            type="number"
            value={student.studentId}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>First Name</label>
          <input
            name="firstName"
            value={student.firstName}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Last Name</label>
          <input
            name="lastName"
            value={student.lastName}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Email</label>
          <input
            name="email"
            type="email"
            value={student.email}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Date of Birth</label>
          <input
            name="dob"
            type="date"
            value={student.dob ? new Date(student.dob).toISOString().split('T')[0] : ''}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Course</label>
          <input
            name="course"
            value={student.course}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Enrollment Year</label>
          <input
            name="enrollmentYear"
            type="number"
            value={student.enrollmentYear}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Status</label>
          <select
            name="status"
            value={student.status}
            onChange={handleChange}
            required
          >
            <option value="Active">Active</option>
            <option value="Inactive">Inactive</option>
          </select>
        </div>

        <button type="submit">Update</button>
      </form>
    </div>
  );
}

export default EditStudent;
