import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function AddStudent() {
  const [student, setStudent] = useState({
    studentId: '',
    firstName: '',
    lastName: '',
    email: '',
    dob: '',
    course: '',
    enrollmentYear: '',
    status: 'Active',
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setStudent({ ...student, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('http://localhost:5000/students', student)
      .then(() => {
        alert('Student added successfully!');
        navigate('/students');
      })
      .catch(err => {
        console.error(err);
        alert('Error adding student');
      });
  };

  return (
    <form onSubmit={handleSubmit}>
      <input name="studentId" placeholder="Student ID" value={student.studentId} onChange={handleChange} required />
      <input name="firstName" placeholder="First Name" value={student.firstName} onChange={handleChange} required />
      <input name="lastName" placeholder="Last Name" value={student.lastName} onChange={handleChange} required />
      <input name="email" placeholder="Email" value={student.email} onChange={handleChange} required />
      <input type="date" name="dob" value={student.dob} onChange={handleChange} required />
      <input name="course" placeholder="Course" value={student.course} onChange={handleChange} required />
      <input name="enrollmentYear" placeholder="Enrollment Year" value={student.enrollmentYear} onChange={handleChange} required />
      <select name="status" value={student.status} onChange={handleChange}>
        <option value="Active">Active</option>
        <option value="Inactive">Inactive</option>
      </select>
      <button type="submit">Add</button>
    </form>
  );
}

export default AddStudent;
