import React, { useState } from 'react';
import axios from '../api';
import { useNavigate } from 'react-router-dom';

function AddStudent() {
  const [formData, setFormData] = useState({
    studentId: '',
    firstName: '',
    lastName: '',
    email: '',
    dob: '',
    department: '',
    enrollmentYear: '',
    isActive: true
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Inside handleSubmit:
await axios.post('/api/students', formData); // Ensure the correct endpoint
navigate('/view-students'); // Correct the path
    } catch (err) {
      alert('Error adding student: ' + err.message);
    }
  };

  return (
    <div className="card p-4 mx-auto" style={{ maxWidth: '600px' }}>
      <h3 className="mb-4">Add New Student</h3>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label>Student ID</label>
          <input className="form-control" name="studentId" value={formData.studentId} onChange={handleChange} required />
        </div>
        <div className="mb-3">
          <label>First Name</label>
          <input className="form-control" name="firstName" value={formData.firstName} onChange={handleChange} required />
        </div>
        <div className="mb-3">
          <label>Last Name</label>
          <input className="form-control" name="lastName" value={formData.lastName} onChange={handleChange} required />
        </div>
        <div className="mb-3">
          <label>Email</label>
          <input type="email" className="form-control" name="email" value={formData.email} onChange={handleChange} required />
        </div>
        <div className="mb-3">
          <label>DOB</label>
          <input type="date" className="form-control" name="dob" value={formData.dob} onChange={handleChange} />
        </div>
        <div className="mb-3">
          <label>Department</label>
          <input className="form-control" name="department" value={formData.department} onChange={handleChange} />
        </div>
        <div className="mb-3">
          <label>Enrollment Year</label>
          <input type="number" className="form-control" name="enrollmentYear" value={formData.enrollmentYear} onChange={handleChange} />
        </div>
        <div className="form-check mb-3">
          <input type="checkbox" className="form-check-input" name="isActive" checked={formData.isActive} onChange={handleChange} />
          <label className="form-check-label">Is Active?</label>
        </div>
        <button type="submit" className="btn btn-success w-100">Add Student</button>
      </form>
    </div>
  );
}

export default AddStudent;
