import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';

function EditStudent() {
  const [student, setStudent] = useState({ name: '', email: '', age: '', course: '' });
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    axios.get(`http://localhost:5000/students/${id}`)
      .then(res => setStudent(res.data))
      .catch(err => alert('Error fetching student: ' + err.message));
  }, [id]);

  const handleChange = (e) => setStudent({ ...student, [e.target.name]: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.put(`http://localhost:5000/students/${id}`, student)
      .then(() => {
        alert('Student updated!');
        navigate('/students');
      })
      .catch(err => alert('Error updating student: ' + err.message));
  };

  return (
    <div>
      <h2>Edit Student</h2>
      <form onSubmit={handleSubmit}>
        <input name="name" value={student.name} onChange={handleChange} required />
        <input name="email" value={student.email} onChange={handleChange} required />
        <input name="age" type="number" value={student.age} onChange={handleChange} required />
        <input name="course" value={student.course} onChange={handleChange} required />
        <button type="submit">Update</button>
      </form>
    </div>
  );
}

export default EditStudent;
