import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './FormStyles.css';

function AddStudent() {
  const [student, setStudent] = useState({ firstName: '', lastName: '', email: '', dob: '', course: '' });
  const navigate = useNavigate();

  const handleChange = (e) => setStudent({ ...student, [e.target.name]: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();

    // Create properly formatted student object
    const formattedStudent = {
      ...student,
      dob: new Date(student.dob).toISOString(), // Convert to ISO String
    };

    axios.post('http://localhost:5000/students', formattedStudent)
      .then(() => {
        alert('Student added successfully!');
        navigate('/students');
      })
      .catch(err => alert('Error adding student: ' + err.message));
};
  return (
    <div className="form-container">
      <h2>Add Student</h2>
      <form onSubmit={handleSubmit} className="student-form">
        <div className="form-group">
          <label>First Name</label>
          <input name="firstName" value={student.firstName} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Last Name</label>
          <input name="lastName" value={student.lastName} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Email</label>
          <input name="email" type="email" value={student.email} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Date of Birth</label>
          <input name="dob" type="date" value={student.dob} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Course</label>
          <input name="course" value={student.course} onChange={handleChange} required />
        </div>
        <button type="submit">Add</button>
      </form>
    </div>
  );
}

export default AddStudent;
