import React, { useState } from 'react';
import axios from '../api';
import { useNavigate } from 'react-router-dom';

function AddStudent() {
  const [formData, setFormData] = useState({
    studentId: '',
    firstName: '',
    lastName: '',
    email: '',
    dob: '',
    department: '',
    enrollmentYear: '',
    isActive: true
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/students', formData);
      navigate('/view-students');
    } catch (err) {
      alert('Error adding student: ' + (err.response?.data?.message || err.message));
    }
  };

  return (
    <div className="card p-4">
      <h3>Add New Student</h3>
      <form onSubmit={handleSubmit}>
        {/* Form fields same as previous version */}
        {/* Make sure to update input types and validation */}
        <button type="submit" className="btn btn-primary mt-3">Add Student</button>
      </form>
    </div>
  );
}

export default AddStudent;
