import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function AddStudent() {
  const [student, setStudent] = useState({ name: '', email: '', age: '', dob: '', course: '' });
  const navigate = useNavigate();

  const handleChange = (e) => setStudent({ ...student, [e.target.name]: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('http://localhost:5000/students', student)
      .then(() => {
        alert('Student added successfully!');
        navigate('/students');
      })
      .catch(err => alert('Error adding student: ' + err.message));
  };

  return (
    <div>
      <h2>Add Student</h2>
      <form onSubmit={handleSubmit}>
        <input name="name" placeholder="Name" value={student.name} onChange={handleChange} required />
        <input name="email" placeholder="Email" value={student.email} onChange={handleChange} required />
        <input name="age" placeholder="Age" type="number" value={student.age} onChange={handleChange} required />
        <input name="dob" placeholder="Date of Birth" type="date" value={student.dob} onChange={handleChange} required />
        <input name="course" placeholder="Course" value={student.course} onChange={handleChange} required />
        <button type="submit">Add</button>
      </form>
    </div>
  );
}

export default AddStudent;
