import React, { useState } from 'react';
import api from '../api';
import { useNavigate } from 'react-router-dom';

function AddStudent() {
  const [formData, setFormData] = useState({
    studentId: '', firstName: '', lastName: '', email: '',
    dob: '', department: '', enrollmentYear: '', isActive: true
  });
  const navigate = useNavigate();

  const handleChange = e => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({ ...prev, [name]: type === 'checkbox' ? checked : value }));
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      await api.post('/students', formData);
      navigate('/');
    } catch (error) {
      console.error('Error adding student', error);
    }
  };

  return (
    <div className="container mt-4">
      <h2>Add Student</h2>
      <form onSubmit={handleSubmit}>
        {['studentId', 'firstName', 'lastName', 'email', 'dob', 'department', 'enrollmentYear'].map(field => (
          <div className="mb-3" key={field}>
            <label className="form-label">{field}</label>
            <input type={field === 'dob' ? 'date' : 'text'} className="form-control"
                   name={field} value={formData[field]} onChange={handleChange} required />
          </div>
        ))}
        <div className="form-check mb-3">
          <input className="form-check-input" type="checkbox" name="isActive"
                 checked={formData.isActive} onChange={handleChange} />
          <label className="form-check-label">Active</label>
        </div>
        <button type="submit" className="btn btn-primary">Add</button>
      </form>
    </div>
  );
}

export default AddStudent;
