import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './FormStyles.css';

function AddStudent() {
  const [student, setStudent] = useState({
    firstName: '',
    lastName: '',
    email: '',
    dob: '',
    course: '',
    enrollmentYear: '',  // Add enrollmentYear
    status: 'Active',
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    setStudent({ ...student, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    axios.post('http://localhost:5000/students', student)
      .then(() => {
        alert('Student added successfully!');
        navigate('/students');
      })
      .catch(err => {
        console.error(err.response?.data || err.message);
        alert('Error adding student: ' + (err.response?.data?.error || err.message));
      });
  };

  return (
    <div className="form-container">
      <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>Add Student</h2>
      <form onSubmit={handleSubmit} className="student-form">

        <div className="form-group">
          <label>First Name</label>
          <input name="firstName" value={student.firstName} onChange={handleChange} required />
        </div>

        <div className="form-group">
          <label>Last Name</label>
          <input name="lastName" value={student.lastName} onChange={handleChange} required />
        </div>

        <div className="form-group">
          <label>Email</label>
          <input name="email" type="email" value={student.email} onChange={handleChange} required />
        </div>

        <div className="form-group">
          <label>Date of Birth</label>
          <input name="dob" type="date" value={student.dob} onChange={handleChange} required />
        </div>

        <div className="form-group">
          <label>Course</label>
          <input name="course" value={student.course} onChange={handleChange} required />
        </div>

        <div className="form-group">
          <label>Enrollment Year</label>
          <input name="enrollmentYear" type="number" value={student.enrollmentYear} onChange={handleChange} placeholder="Enter Enrollment Year" required />
        </div>

        <div className="form-group">
          <label>Status</label>
          <select name="status" value={student.status} onChange={handleChange} required>
            <option value="Active">Active</option>
            <option value="Inactive">Inactive</option>
          </select>
        </div>

        <button type="submit">Add</button>
      </form>
    </div>
  );
}

export default AddStudent;
