import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './FormStyles.css'; // our simple CSS

function AddStudent() {
  const [student, setStudent] = useState({
    studentId: '',
    firstName: '',
    lastName: '',
    email: '',
    dob: '',
    course: '',
    enrollmentYear: '',
    status: 'Active',
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setStudent({ ...student, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('http://localhost:5000/students', student)
      .then(() => {
        alert('Student added successfully!');
        navigate('/students');
      })
      .catch(err => {
        console.error(err);
        alert('Error adding student');
      });
  };

  return (
    <div className="form-container">
      <h2>Add New Student</h2>
      <form onSubmit={handleSubmit} className="student-form">

        <label>Student ID</label>
        <input type="text" name="studentId" value={student.studentId} onChange={handleChange} required />

        <label>First Name</label>
        <input type="text" name="firstName" value={student.firstName} onChange={handleChange} required />

        <label>Last Name</label>
        <input type="text" name="lastName" value={student.lastName} onChange={handleChange} required />

        <label>Email</label>
        <input type="email" name="email" value={student.email} onChange={handleChange} required />

        <label>Date of Birth</label>
        <input type="date" name="dob" value={student.dob} onChange={handleChange} required />

        <label>Course</label>
        <input type="text" name="course" value={student.course} onChange={handleChange} required />

        <label>Enrollment Year</label>
        <input type="text" name="enrollmentYear" value={student.enrollmentYear} onChange={handleChange} required />

        <label>Status</label>
        <select name="status" value={student.status} onChange={handleChange}>
          <option value="Active">Active</option>
          <option value="Inactive">Inactive</option>
        </select>

        <button type="submit">Add Student</button>
      </form>
    </div>
  );
}

export default AddStudent;
